(()=>{window.addEventListener("DOMContentLoaded",()=>{let o=document.querySelectorAll("section[id]"),i=new IntersectionObserver(e=>{e.forEach(n=>{let r=n.target.getAttribute("id"),t=document.querySelector(`#TableOfContents li a[href="#${r}"]`);t&&(n.intersectionRatio>0?t.parentElement.classList.add("active"):t.parentElement.classList.remove("active"))})});o.forEach(e=>{i.observe(e)})});})();
